// using useReducer hook
  const[emailState,dispatchEmail]=useReducer(reducer,{val:"",isvalid:null})
  const[passwordState,dispatchPassword]=useReducer(reducer,{val:" ",isvalid:null})

  const emailChangeHandler = (event) => {
    dispatchEmail({type:"USER_INPUT",val:event.target.value})
    setFormIsValid(
      emailState.isvalid && passwordState.isvalid
