import React, { useEffect, useState } from "react"

//to bulid custom component on context file
const AuthContextProvider=(props)=>{
    const [isLoggedIn, setIsLoggedIn] = useState(false);
  
      useEffect(()=>{
        const getLocalStorageLoggedIn=localStorage.getItem("isLoggedIn");

        if (getLocalStorageLoggedIn === "1") {
         setIsLoggedIn(true);
        } 
      },[])


      const loginHandler = (email, password) => {
        // We should of course check email and password
        // But it's just a dummy/ demo anyways
        setIsLoggedIn(true);
        localStorage.setItem("isLoggedIn","1");
      };

      const logoutHandler = () => {
        setIsLoggedIn(false);
        localStorage.removeItem("isLoggedIn");
      };


    return(
    <AuthContextProvider  isLoggedIn={isLoggedIn} onLogout={logoutHandler} onLogin={loginHandler}>
    {props.children}</AuthContextProvider>)
}

const AuthContext=React.createContext(
    {
        isLoggedIn :false,
        onLogout:()=>{}
    }
)

export default AuthContext;
