import React, { useContext, useEffect, useReducer, useState } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

const emailReducer = (state, action) => {
    if (action.type === "USER_INPUT") {
        return { val: action.val, isvalid: action.val.includes("@") }
    }

    if (action.type === "INPUT_BLUR") {
        return ({ val: state.val, isvalid: state.val.includes("@") })
    }
    return { val: "", isvalid: null }
}

const passwordReducer = (state, action) => {
    if (action.type === "USER_PASSWORD") {
        return ({
            val: action.val,
            isvalid: action.val.trim().length > 6
        })
    }

    if (action.type === "PASSWORD_BLUR") {
        return ({
            val: state.val,
            isvalid: state.val.trim().length > 6
        })
    }

    return ({
        val: "",
        isvalid: null
    })
}

const Login = (props) => {
    // const [enteredEmail, setEnteredEmail] = useState('');
    // const [emailIsValid, setEmailIsValid] = useState();
    // const [enteredPassword, setEnteredPassword] = useState('');
    // const [passwordIsValid, setPasswordIsValid] = useState();
    const [formIsValid, setFormIsValid] = useState(false);
    const authCtx=useContext(AuthContext);

    // useEffect(()=>{
    //   const { isValid: emailIsValid } = emailState;
    //   const { isValid: passwordIsValid } = passwordState;
    //   const setTimeOutFun= setTimeout(()=>{
    //     setFormIsValid(
    //       emailIsValid && passwordIsValid
    //      );
    //      console.log("hello");
    //   },500);
    //   //useEffect clear function
    //   return ()=>{
    //     clearTimeout(setTimeOutFun);
    //   }
    // },[emailIsValid,passwordIsValid]);

    // using useReducer hook
    const [emailState, dispatchEmail] = useReducer(emailReducer, { val: "", isvalid: null })
    const [passwordState, dispatchPassword] = useReducer(passwordReducer, { val: " ", isvalid: null })

    const emailChangeHandler = (event) => {
        dispatchEmail({ type: "USER_INPUT", val: event.target.value })
        setFormIsValid(
            emailState.isvalid && passwordState.isvalid
        );
    };

    const passwordChangeHandler = (event) => {
        dispatchPassword({ type: "USER_PASSWORD", val: event.target.value })
        setFormIsValid(
            emailState.isvalid && passwordState.isvalid
        );
    };

    const validateEmailHandler = () => {
        dispatchEmail({ type: "INPUT_BLUR" })
    };

    const validatePasswordHandler = () => {
        dispatchPassword({ type: "PASSWORD_BLUR" });
    };

    const submitHandler = (event) => {
        event.preventDefault();
        props.onLogin(emailState.val, passwordState);
    };

    return ( <
        Card className = { classes.login } >
        <
        form onSubmit = { submitHandler } >
        <
        div className = { `${classes.control} ${
            emailState.isvalid ? classes.isvalid : ''
          }` } >
        <
        label htmlFor = "email" > E - Mail < /label> <
        input type = "email"
        id = "email"
        value = { emailState.val }
        onChange = { emailChangeHandler }
        onBlur = { validateEmailHandler }
        /> <
        /div> <
        div className = { `${classes.control} ${
            passwordState.isvalid? classes.isvalid : ''
          }` } >
        <
        label htmlFor = "password" > Password < /label> <
        input type = "password"
        id = "password"
        onChange = { passwordChangeHandler }
        onBlur = { validatePasswordHandler }
        /> <
        /div> <
        div className = { classes.actions } >
        <
        Button type = "submit"
        className = { classes.btn }
        disabled = {!formIsValid } >
        Login <
        /Button> <
        /div> <
        /form> <
        /Card>
    );
};

export default Login;
